import React, { useState, useEffect } from "react";

const Navbar = () => {
    // Check local storage or default to light mode
    const [darkMode, setDarkMode] = useState(
        localStorage.getItem("theme") === "dark"
    );

    useEffect(() => {
        if (darkMode) {
            document.documentElement.classList.add("dark");
            localStorage.setItem("theme", "dark");
        } else {
            document.documentElement.classList.remove("dark");
            localStorage.setItem("theme", "light");
        }
    }, [darkMode]);

    return (
        <nav className="bg-blue-600 dark:bg-gray-900 p-4 flex justify-between items-center">
            <h1 className="text-white font-bold text-lg">Personal Finance Tracker</h1>

            {/* Navigation Links */}
            <div className="flex space-x-4">
                <a href="#" className="text-white dark:text-gray-300">Dashboard</a>
                <a href="#" className="text-white dark:text-gray-300">Transactions</a>
                <a href="#" className="text-white dark:text-gray-300">Reports</a>
                <a href="#" className="text-white dark:text-gray-300">Profile</a>
            </div>

            {/* Dark Mode Toggle */}
            <button
                onClick={() => setDarkMode(!darkMode)}
                className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-white transition duration-300"
            >
                {darkMode ? "ðŸŒž" : "ðŸŒ™"}
            </button>
        </nav>
    );
};

export default Navbar;
